// Frontend-only Video Progress Tracker (React + Local State)

import React, { useEffect, useRef, useState } from "react";
import "./App.css";

const VIDEO_DURATION = 180; // Example: 2 minutes

function App() {
  const videoRef = useRef();
  const [watched, setWatched] = useState([]);
  const [watching, setWatching] = useState(new Set());
  const [intervalTimer, setIntervalTimer] = useState(null);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("progressData"));
    if (stored) {
      setWatched(stored.intervals || []);
      videoRef.current.currentTime = stored.lastPosition || 0;
    }
  }, []);

  const startTracking = () => {
    const timer = setInterval(() => {
      const time = Math.floor(videoRef.current.currentTime);
      setWatching((prev) => new Set(prev).add(time));
    }, 500);
    setIntervalTimer(timer);
  };

  const stopTracking = () => {
    clearInterval(intervalTimer);
    const seconds = Array.from(watching).sort((a, b) => a - b);
    const merged = [];
    for (let i = 0; i < seconds.length; i++) {
      let start = seconds[i];
      while (i < seconds.length - 1 && seconds[i + 1] === seconds[i] + 1) i++;
      let end = seconds[i] + 1;
      merged.push([start, end]);
    }
    const all = mergeIntervals([...watched, ...merged]);
    const lastPosition = videoRef.current.currentTime;
    setWatched(all);
    setWatching(new Set());
    localStorage.setItem(
      "progressData",
      JSON.stringify({ intervals: all, lastPosition })
    );
  };

  const mergeIntervals = (intervals) => {
    intervals.sort((a, b) => a[0] - b[0]);
    const merged = [];
    for (const [start, end] of intervals) {
      if (!merged.length || merged[merged.length - 1][1] < start) {
        merged.push([start, end]);
      } else {
        merged[merged.length - 1][1] = Math.max(
          merged[merged.length - 1][1],
          end
        );
      }
    }
    return merged;
  };

  const getProgress = () => {
    const uniqueSeconds = watched.reduce((acc, [s, e]) => acc + (e - s), 0);
    return ((uniqueSeconds / VIDEO_DURATION) * 100).toFixed(2);
  };

  return (
    <div className="app-container">
      <header className="navbar">
        <h2>Video Progress Tracker</h2>
      </header>

      <main className="main-content">
        <div className="video-container">
          <video
            ref={videoRef}
            width="600"
            controls
            onPlay={startTracking}
            onPause={stopTracking}
            onEnded={stopTracking}
src="/video/" > </video>
          <div className="progress-display">Progress: {getProgress()}%</div>
        </div>
      </main>

      <footer className="footer">
        <p>&copy; 2025 | Built for SDE Intern Assignment</p>
      </footer>
    </div>
  );
}

export default App;
